import { Component, Input, OnChanges, OnInit } from '@angular/core';
import { ApiService } from './ApiService';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent implements OnInit {
  weather: any;
  isim: any;
  anlik_bilgiler: any;
  saat: any;
  dakika: any;
  yil: any;
  gun: any;
  ay: any;
  sicaklik: any;
  hava: any;
  resim: any;
  selectedCity = '';
  yeni_bilgiler: any;



  onSearchClick(){
    console.log("başarılı");
  }
  constructor(private apiService: ApiService) { }





  ngOnInit(): void {
    this.apiService.getApiData(this.selectedCity).subscribe(data => {
      this.weather = data;
      this.isim = this.weather.data[0].city_name;
      this.anlik_bilgiler = this.weather.data[0];
      console.log(this.weather);
      let zaman = new Date()
      this.saat = zaman.getHours();
      this.dakika = zaman.getMinutes();
      this.yil = zaman.getFullYear();
      this.ay = zaman.getMonth() + 1;
      this.gun = zaman.getDay();
      this.sicaklik = this.weather.data[0].app_temp;
      this.hava = this.weather.data[0].weather.description;
      this.yeni_bilgiler = this.weather.data[0];

      let resimler = [
        { durum: 'Scattered clouds', url: 'https://st2.depositphotos.com/1064969/42557/v/1600/depositphotos_425573118-stock-illustration-weather-forecast-partly-cloudy-icon.jpg' },
        { durum: 'Haze', url: 'https://images.freejpgimages.com/vme/images/1/6/166202/symbols_weather_clear_sunny_preview.' },
        { durum: 'rainy', url: 'https://previews.123rf.com/images/carbo82/carbo821206/carbo82120600075/14181786-simge-ya%C4%9F%C4%B1%C5%9Fl%C4%B1-hava.jpg' },
        { durum: 'cloudy', url: 'https://images.freeimages.com/cme/images/istock/previews/7126/71264247-cloudy-icon-overcast-weather-sign.jpg' },


      ];

      let resimUrl = resimler.find(resim => resim.durum === this.hava);
      console.log(this.weather);

      if (resimUrl) {
        this.resim = resimUrl.url;
      } else {
        console.error("resim bulunamadı...");
      }


    });
  }
}
