import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

@Injectable({
 providedIn: 'root'
})
export class ApiService {
 private apiUrl = 'https://api.collectapi.com/weather/getWeather?';
 private apiKey = 'apikey 4bxhu6WKdwYK8bzSLOYcpB:5DB0cRCFKEP2b4bQnX7ckZ';

 constructor(private http: HttpClient) { }

 getApiData(): Observable<any> {
    return this.http.get(this.apiUrl, { headers: { 'Authorization': `Bearer ${this.apiKey}` } });
    
 }
}
