import { Component, OnInit } from '@angular/core';
import { ApiService } from './ApiService';

@Component({
 selector: 'app-weather',
 templateUrl: './weather.component.html',
 styleUrls: ['./weather.component.css']
})
export class WeatherComponent implements OnInit {
 weather: any;
 isim:any;
 anlik_bilgiler:any;
 saat:any;
 dakika:any;
 yil:any;
 gun:any;
 ay:any;
 sicaklik:any;
 hava:any;
 resim:any;

 constructor(private weatherService: ApiService) { }

 ngOnInit(): void {
    this.weatherService.getApiData().subscribe(data => {
      this.weather = data;
      this.isim = this.weather.data[0].city_name;
      this.anlik_bilgiler = this.weather.data[0]
      console.log(this.weather);
      let zaman=new Date()
      this.saat=zaman.getHours();
      this.dakika=zaman.getMinutes();
      this.yil=zaman.getFullYear();
      this.ay=zaman.getMonth()+1;
      this.gun=zaman.getDay();
      this.sicaklik=this.weather.data[0].app_temp;
      this.hava=this.weather.data[0].weather.description;


      if (this.hava==="Scattered clouds") {
        this.resim="https://cdn.create.vista.com/api/media/medium/219189554/stock-vector-cloudy-day-isolated-vector-icon-which-can-easily-edit-modified?token="
      }




    });
 }
}
