  styleUrls: ['./to-do.component.css']
})
export class ToDoComponent {
  items=[
    {description:"kahvaltı",action:"No"},
    {description:"Kod",action:"No"},
    {description:"Ders",action:"No"},
    {description:"Gitar",action:"No"},
  ];
}
