import { Component } from '@angular/core';



@Component({
  selector: 'app-hesap',
  templateUrl: './hesap.component.html',
  styleUrls: ['./hesap.component.css']
})


export class HesapComponent {
  result = 0;
  operation = '';
  firstOperand = 0;
  secondOperand = 0;
  input = '';

  // Define the button click events for each button
  numberClick(number:any) {
     if (this.operation === '') {
       this.result = number;
     } else {
       this.input += number;
     }
  }

  decimalClick() {
     if (this.operation === '') {
       this.result = parseFloat(this.result + '.');
     } else {
       this.input += '.';
     }
  }

  operationClick(operator:any) {
     if (this.operation === '') {
       this.firstOperand = this.result;
       this.result = 0;
       this.operation = operator;
     } else {
       this.secondOperand = parseFloat(this.input);
       this.performOperation();
       this.operation = operator;
       this.input = '';
     }
  }

  equalClick() {
     if (this.operation !== '') {
       this.secondOperand = parseFloat(this.input);
       this.performOperation();
       this.operation = '';
       this.input = '';
     }
  }

  performOperation() {
     switch (this.operation) {
       case '+':
         this.result = this.firstOperand + this.secondOperand;
         break;
       case '-':
         this.result = this.firstOperand - this.secondOperand;
         break;
       case 'x':
         this.result = this.firstOperand * this.secondOperand;
         break;
       case '/':
         this.result = this.firstOperand / this.secondOperand;
         break;
       case '%':
         this.result = this.firstOperand % this.secondOperand;
         break;
     }
  }

  // Additional button click events for special functions
  acClick() {
     this.result = 0;
     this.operation = '';
     this.firstOperand = 0;
     this.secondOperand = 0;
     this.input = '';
  }

  positiveNegativeClick() {
     this.result = -this.result;
  }

  // Define the display logic
  display() {
     if (this.operation === '') {
       return this.result;
     } else {
       return this.input;
     }
  }
 }
