import { Component, OnInit } from '@angular/core';
import { ApiService } from './ApiService';

@Component({
 selector: 'app-weather',
 templateUrl: './weather.component.html',
 styleUrls: ['./weather.component.css']
})
export class WeatherComponent implements OnInit {
 weather: any;
 isim:any;
 anlik_bilgiler:any;
 saat:any;
 dakika:any;
 yil:any;
 gun:any;
 ay:any;
 constructor(private weatherService: ApiService) { }

 ngOnInit(): void {
    this.weatherService.getApiData().subscribe(data => {
      this.weather = data;
      this.isim = this.weather.data[0].city_name;     
      this.anlik_bilgiler = this.weather.data[0]     
      console.log(this.weather);
      let zaman=new Date()
      this.saat=zaman.getHours();
      this.dakika=zaman.getMinutes();
      this.yil=zaman.getFullYear();
      this.ay=zaman.getMonth();
      this.gun=zaman.getDay();




    });
 }
}
